package classDemo;

public class VarDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int firstNumb = 5; // "declaration"
		int secondNumb = 10; // "=" is called "assignment operator"
		firstNumb = secondNumb;
		
		double muDouble = 5.98;
		char myChar = 'a'; // only use a single quotes
		boolean myBool = true;
				
		System.out.println("first number is: " + firstNumb); // shortcut: "sysout" + Ctrl + Space
		//System.out.println(secondNumb);
		System.out.println("first double is: " + muDouble);
		System.out.println("first char is: " + myChar);
		System.out.println("first boolean is: " + myBool);
	}

}
